<?phpnamespace admin;use Move\db;class game extends admin{	public function __construct()	{		parent::__construct();		echo 'game';		die;	}	public function index()	{		$this->display();	}	public function game_status()	{		if (APP_DEMO) {			ajax('测试站暂时不能修改');		}		$id = iv('get.id', 'd', '参数错误');		$status = iv('get.status', 'd', '参数错误');		$game = db::table('version_game')->where(['id' => $id])->find();		if (!$game) {			ajax('应用不存在');		}		$res = db::table('version_game')->where(['id' => $id])->save(['status' => $status]);		if ($res) {			ajax('操作成功', 1);		} else {			ajax('操作失败');		}	}}