<?phpnamespace admin;use Move\db;class huodong extends admin{	public function __construct()	{		parent::__construct();		if (!$this->is_game('huodong')) {			ajax('这个应用你没有购买');		}	}	public function index()	{		if (empty($_POST)) {			$data['list'] = md('sys_config')->lists();			$builder = new builder();			$builder->data($data)->url()->title('活动配置', '这里是活动配置')->edit();		} else {			if (APP_DEMO) {				ajax('测试站暂时不能修改');			}		}	}}