<?phpnamespace admin;use Move\db;class wap extends admin{	public function __construct()	{		parent::__construct();		if (!$this->is_game('wap')) {			ajax('这个应用你没有购买');		}	}	public function index()	{		if (empty($_POST)) {			$data['list'] = md('sys_config')->lists();			$builder = new builder();			$builder->data($data)->url()->title('WAP配置', '这里是WAP配置')->key('wap_close', 'wap网站状态', 'select', [0 => '禁止访问', 1 => '正常访问'], 'wap_close状态')->key('wap_tz', '自动跳转', 'select', [0 => '禁止', 1 => '开启'], '手机访问自动跳转')->edit();		} else {			if (APP_DEMO) {				ajax('测试站暂时不能修改');			}			$data['wap_close'] = iv('post.wap_close');			$data['wap_tz'] = iv('post.wap_tz');			$res = 0;			foreach ($data as $k => $v) {				if (md('sys_config')->setValueOrName($k, $v)) {					$res = 1;				}			}			if ($res) {				ajax('操作成功', 1);			} else {				ajax('操作失败');			}		}	}}