<?php$key = '';$secret_key = '';$userid = '';$url = 'http://demo.movesay.com/api';$nonce = time();echo '<pre>';print_r(_get($url . '/trade/all/'));echo '</pre>';echo '<pre>';$data = 'key=' . $key . '&nonce=' . $nonce . '&signature=' . md5($key . '&' . $userid . '&' . $nonce . '&' . $secret_key);print_r(_post($url . '/trade/balance', $data));echo '</pre>';function _get($url){	if (function_exists('file_get_contents')) {		$file_contents = file_get_contents($url);	} else {		$ch = curl_init();		$timeout = 5;		curl_setopt($ch, CURLOPT_URL, $url);		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);		curl_setopt($ch, CURLOPT_CONNECTTIMEOUT, $timeout);		$file_contents = curl_exec($ch);		curl_close($ch);	}	return $file_contents;}function _post($url, $data_string){	$ch = curl_init();	curl_setopt($ch, CURLOPT_URL, $url);	curl_setopt($ch, CURLOPT_POSTFIELDS, $data_string);	curl_setopt($ch, CURLOPT_HEADER, false);	curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);	curl_setopt($ch, CURLOPT_NOBODY, true);	curl_setopt($ch, CURLOPT_POST, true);	$return_str = curl_exec($ch);	curl_close($ch);	return $return_str;} 