<?phpnamespace app;use Move\db;class cen_qu extends home{	public function __construct()	{		parent::__construct();	}	public function index()	{		$btc = db::table("cen_btc")->where(['pid' => 0])->field('id,pid,title')->select();		foreach ($btc as $k => $v) {			$btc[$k]['tit'] = db::table('cen_btc')->where(['pid' => $v['id']])->field('id,pid,title')->select();			foreach ($btc[$k]['tit'] as $kk => $vv) {				$btc[$k]['tit'][$kk]["tit1"] = db::table('cen_btc')->where(['pid' => $vv['id']])->field('id,pid,title,url')->select();			}		}		ajax($btc, 1);	}} 