<?phpnamespace app;use Move\db;use Move\ext\page;class charge extends home{	public function __construct()	{		parent::__construct();		$this->userid = $this->userid();	}	public function index()	{		$this->set_title('卡密充值');	}	public function log()	{		$this->set_title('充值记录');		if ($this->userid) {			$where = ["userid" => $this->userid, "status#!=" => 0];			$res = parent::pullpage("charge_log", $where, "id desc");			$list = $res['list'];			foreach ($list as $k => $v) {				$username = db::table("user")->where(["id" => $v["userid"]])->find();				$list[$k]["username"] = substr_replace(str_replace("|", " ", $username['username']), '**', 3, 2);				$list[$k]["addtime"] = addtime($list[$k]["addtime"]);				if ($list[$k]["gettime"]) {					$list[$k]["gettime"] = addtime($list[$k]["gettime"]);				}			}		} else {			$list = [];		}		$res['list'] = $list;		ajax($res, 1);	}	public function up()	{		if (!$this->userid) {			ajax("请先登录");		}		$cardnum = iv('post.cardnum', "dw", "卡号格式错误");		$password = iv('post.paypassword', "dw", "密码格式错误");		if (!$this->userid) {			ajax("请先登录");		}		$user = db::table('user')->where(['id' => $this->userid])->find();		if (!$user) {			ajax('用户异常');		}		$res = md('charge')->up($this->userid, $cardnum, $password);		if (isset($res[1]) && $res[1]) {			ajax($res[0], 1);		} else {			ajax($res[0]);		}	}} 