<?phpnamespace ext\JPush;class run{	public function __construct()	{		spl_autoload_register(function ($class) {			preg_match("/JPush/", $class, $match);			if (isset($match[0]) && $match[0]) {				$path = str_replace('\\', DIRECTORY_SEPARATOR, $class);				$file = APP_PATH . '/controller/ext/' . $path . '.php';				if (file_exists($file)) {					require_once $file;				}			}		});	}} 