<?phpnamespace ext;use Move\db;class email{	function sendemail($addr, $email_config)	{		require APP_PATH . '/controller/ext/mail/class.phpmailer.php';		$mail = new \PHPMailer(true);		$mail->CharSet = "UTF-8";		$mail->IsSMTP();		$mail->Host = $email_config['host'];		$mail->SMTPAuth = true;		$mail->Username = $email_config['username'];		$mail->Password = $email_config['password'];		if (defined("MAIL_SSL")) {			$mail->SMTPSecure = "ssl";			$mail->Port = 465;		} else {			$mail->Port = 25;		}		$mail->From = $email_config['username'];		$mail->AddAddress($addr['email'], $addr['name']);		$mail->FromName = $addr['title'];		$mail->IsHTML(true);		$mail->Subject = $addr['title'];		$mail->Body = $addr['content'];		if (!$mail->Send()) {			return "邮件发送失败: " . $mail->ErrorInfo;		} else {			return ['邮件发送成功', 1];		}	}}