<?phpnamespace ext;use Move\db;class excel{	public function __construct()	{	}	public function export($file_name, $title, $data)	{		header('Content-Type: application/vnd.ms-excel');		header("Content-Disposition: attachment;filename = {$file_name}.csv");		header('Cache-Control: max-age=0');		$fp = fopen('php://output', 'a');		foreach ($title as $i => $one) {			$head[$i] = iconv('utf-8', 'gbk', $one);		}		fputcsv($fp, $head);		foreach ($data as $one) {			$row = [];			foreach ($one as $j => $v) {				$row[$j] = "\t" . iconv('utf-8', 'gbk', $v);			}			fputcsv($fp, $row);		}		fputcsv($fp, ['']);		fputcsv($fp, ['']);		fputcsv($fp, ['']);	}}