<?phpif (!defined("APP_PATH")) exit("no direct access allowed");function classLoader($class){	$path = str_replace('\\', DIRECTORY_SEPARATOR, $class);	$file = __DIR__ . DIRECTORY_SEPARATOR . DIRECTORY_SEPARATOR . $path . '.php';	if (file_exists($file)) {		require_once $file;	}}spl_autoload_register('classLoader');class OssClient{	public static $config = "";	public static $handel = "";	public static $bucket = "";	public static function setConfig($config)	{		self::$config = $config;	}	public static function init()	{		if (!self::$handel) {			if (!self::$config) {				exit("OSS\OssClient init 未配置!");			}			$config = self::$config;			self::$bucket = $config['OSS_TEST_BUCKET'];			self::$handel = new OSS\OssClient($config['OSS_ACCESS_ID'], $config['OSS_ACCESS_KEY'], $config['OSS_ENDPOINT'], false);		}		if (!self::$handel) {			exit("OSS\\OssClient error!");		}		return self::$handel;	}	public static function getBucket()	{		return self::$bucket;	}} 