<?phpif (!defined("APP_PATH")) exit("no direct access allowed");if (!isset($_GET['object'])) {	die("object 必须存在");}$object = trim($_GET['object']);require_once __DIR__ . '/common.php';if (!isset($list_conf)) {	exit("access deny!");}OssClient::setConfig($list_conf);$ossClient = OssClient::init();$ossClient->deleteObject(OssClient::getBucket(), $object);if (isset($_SERVER['HTTP_REFERER'])) {	header("Location:" . $_SERVER['HTTP_REFERER']);} else {	echo "删除成功!";}