<?phpnamespace home;use Move\db;class cen_qu extends home{	public function __construct()	{		parent::__construct();	}	public function index()	{		$btc = db::table("cen_qu_type")->where(['pid' => 0, 'status' => 1])->field('id,pid,title')->select();		if ($btc) {			foreach ($btc as $k => $v) {				$btc[$k]['tit'] = db::table('cen_qu_type')->where(['pid' => $v['id'], 'status' => 1])->field('id,pid,title')->select();				if ($btc) {					foreach ($btc[$k]['tit'] as $kk => $vv) {						$btc[$k]['tit'][$kk]["tit1"] = db::table('cen_qu')->where(['type_id' => $vv['id'], 'status' => 1])->field('id,type_id,title,url')->select();					}				}			}		}		$this->assign('btc', $btc);		$this->display();	}} 