<?phpnamespace home;use Move\db;use Move\ext\page;class coin extends home{	public function __construct()	{		parent::__construct();		$this->is_game('coin');	}	public function index()	{		$this->set_title('币种管理');		$where = [];		$mymc_coin = new \mymc\coin();		$coin = $mymc_coin->select();		$count = count($coin);		$PageObj = new page($count, 15);		$show = $PageObj->show();		$list = db::table("coin")->where($where)->order("`id` desc")->limit($PageObj->firstRow, $PageObj->listRows)->select();		$this->pages = ['show' => $show, 'list' => $list];		$this->display();	}}