<?phpnamespace home;use Move\db;use Move\config;use Move\cache;use move\ext\page;class lendone extends home{	public function __construct()	{		parent::__construct();		$this->is_game('lendone');	}	public function index()	{		show_msg("开发中......");		$this->set_title('理财中心');		$lend_qi = db::table("lend_qi")->where(["status" => 1])->select();		if (session("userid")) {			$userdata = db::table("user")->where(["id" => session("userid")])->field("id,username")->find();			$this->assign("userdata", $userdata);		}		$this->assign("lend_qi", $lend_qi);		$this->display();	}}