<?phpnamespace home;use Move\mvc\controller;class okcoin extends controller{	public function __construct()	{		parent::__construct();	}	public function okcoin()	{		$coin = iv('post.coin', 'w', 'coin err');		$num = iv('post.num', 'xnb', 'num err');		$url = 'https://www.okcoin.cn/api/v1/ticker.do?symbol=' . $coin . '_cny';		$data = json_decode(file_get_contents($url), true);		if (isset($data['ticker']) && isset($data['ticker']['low']) && $data['ticker']['low']) {			$money = $num * $data['ticker']['low'];			ajax('success', 1, $money);		} else {			$money = '';			ajax('fail', 0, $money);		}	}} 