<?phpnamespace home;use Move\db;class spread extends home{	public function __construct()	{		parent::__construct();	}	public function index()	{		if (isset($_GET['inivt']) && $_GET['inivt']) {			$this->invit = $_GET['inivt'];		}		$this->display();	}	public function detail()	{		if (isset($_GET['inivt']) && $_GET['inivt']) {			$this->invit = $_GET['inivt'];		}		$this->display();	}	public function invit_num()	{		$sql = 'select invit_1 ,count(invit_1) as count  from move_user where invit_1 != 0 and addtime > 1502121600 group by invit_1  order by count desc limit 100;';		$res = db::query($sql);		foreach ($res as $k => $v) {			$res[$k]['username'] = db::table('user')->where(['id' => $v['invit_1']])->find('username');			unset($res[$k]['invit_1']);		}		$count = db::table('user')->where(['addtime#>' => '1502121600'])->count();		ajax($res, $count);	}}