<?phpnamespace wap;use Move\db;use move\ext\page;use Move\config;class coin extends wap{	public function __construct()	{		parent::__construct();	}	public function coinIn_h()	{		$this->display();	}	public function coinIn()	{		$this->display();	}	public function coinIn_log_h()	{		$this->display();	}	public function coinIn_log()	{		$this->display();	}	public function coinOut_h()	{		$this->display();	}	public function coinOut()	{		$this->display();	}	public function coinOut_log_h()	{		$this->display();	}	public function coinOut_log()	{		$this->display();	}	public function index()	{		$this->display();	}	public function index_back()	{		$this->market = db::table("trade_market")->field("id,title,name")->where(["status" => 1])->select();		$this->display();	}	public function login()	{		$this->display();	}	public function regist()	{		$this->display();	}	public function marketlist()	{		$market = db::table("trade_market")->field("id,title,name")->where(["status" => 1])->select();		if ($market) {			ajax($market, 1);		} else {			ajax('市场错误');		}	}	public function marketinfo()	{		$marketID = iv('post.market', 'w_', '参数错误');		if (!$marketID) {			ajax('市场参数未传递');		}		$market = db::table("trade_market")->where(["status" => 1, "name" => $marketID])->find();		$minPrice = ($market['min_price'] * 1000 + rand(-9, 9)) / 1000 + 0;		$maxPrice = $minPrice + rand(30, 99) / 1000;		$newPrice = rand($minPrice * 1000 + 9, $maxPrice * 1000 - 9) / 1000;		if ($market) {			$res = ['id' => $market['id'], 'name' => $market['name'], 'title' => $market['title'], 'time' => date("H:i:s", time()), 'min_price' => $minPrice, 'new_price' => $newPrice, 'max_price' => $maxPrice];			ajax($res, 1);		} else {			ajax('市场不存在');		}	}} 