<?phpnamespace model;use Move\db;class finan_pay extends common{	public function get_info($id = NULL)	{		if (!$id) {			return false;		}		$res = db::table("finan_pay")->where(["id" => $id])->find();		if (!empty($res)) {			return $res;		} else {			return false;		}	}	public function lists()	{		$res = db::table('finan_type')->where(['status' => 1])->select();		if ($res) {			return [$res, 1];		} else {			return ['暂无充值方式'];		}	}}