<?phpnamespace model;use Move\db;class user_coin extends common{	public function info($userid = NULL)	{		if (!$userid) {			return false;		}		$res = db::table("user_coin")->where(["userid" => $userid])->find();		if (!empty($res)) {			return $res;		} else {			return false;		}	}	public function get_info($userid = NULL)	{		if (!$userid) {			return false;		}		$res = db::table("user_coin")->where(["userid" => $userid])->find();		if (!empty($res)) {			return $res;		} else {			return false;		}	}}